00244 
00245     if ( center_x < W/2 ) //-W*2/3)
00246         center_x -= static_cast<int>(transpose_ratio_x * (static_cast<float>(W) / 2.0f - static_cast<float>(center_x)));
00247     else if ( center_x > W/2) //*2/3)
00248         center_x += static_cast<int>(transpose_ratio_x * (static_cast<float>(center_x) - static_cast<float>(W) / 2.0f));
00249     if ( center_y < H/2 )
00250         center_y -= static_cast<int>(transpose_ratio_y * (static_cast<float>(H) / 2.0f - static_cast<float>(center_y)));
00251     else if ( center_y > H/2 )
00252         center_y += static_cast<int>(transpose_ratio_y * (static_cast<float>(center_y) - static_cast<float>(H) / 2.0f));
00253 
00254     cv::Point center(W - center_x, center_y);//Declaring the center point - Reflect horizontally
00255     int radius = roiVolumeW > roiVolumeH ? roiVolumeW/2 : roiVolumeH/2; //Declaring the radius
00256     cv::Scalar line_Color(0, 0, 0);//Color of the circle
