00262 void Visualizer::draWaveform(){
00263 
00264     int W=cfg.dispconf.dispResW.load();
00265     int H=cfg.dispconf.dispResH.load();
00266     int cameraW = cfg.camconf.camResW.load();
00267     int cameraH = cfg.camconf.camResH.load();
00268 
00269     int x_centre = W/2; 
00270     int y_centre = H/2; 
00271     int r = (cameraW>cameraH) ? cameraH/2 : cameraW/2;
00272 
00273     float minMax[2];
00274     size_t numSamples;
00275     waveform.getMinMax(minMax);
00276     float min = minMax[0],max = minMax[1];
00277     numSamples = waveform.availableForReading();
00278 
00279 
00280     // depict waveform
00281     size_t end;
00282     double curRadians=0.0;
00283     double radianStep=2*M_PI / (double)numPointsPerimeter;
00284 
00285     if (numSamples< static_cast<size_t>(numPointsPerimeter)){
00286         end=numSamples;
00287     }else end = static_cast<size_t>(numPointsPerimeter);
00288 
00289     int thickness=1;
00290     int x1,x2,y1,y2;
00291     float percent;
00292     float new_radius;
00293     float waveVal;
00294     for (size_t i=0; i<end ; i++){
00295         
00296         // pixels are calculated given the following equations:
00297         // x = cx + r * cos(a)
00298         // y = cy + r * sin(a)
00299         x1 = static_cast<int>((float)r * std::cos(curRadians) + (float)x_centre);
00300         y1 = static_cast<int>((float)r * std::sin(curRadians) + (float)y_centre);
00301     
00302         waveform.read(waveVal);
00303         // normalize in range [-1 1]
00304         percent = 2* ( (waveVal-min) / (max-min) ) -1;
00305 
00306         // trasport x and y
00307         new_radius = static_cast<float>(r + ((float)H / 40. * percent));
00308 
00309         x2 = static_cast<int>(new_radius * std::cos(curRadians) + (float)x_centre);
00310         y2 = static_cast<int>(new_radius * std::sin(curRadians) + (float)y_centre);
00311 
00312         cv::Point p1(x1, y1), p2(x2, y2); 
00313         cv::line(visualFrame, p1, p2, cv::Scalar(255, 0, 0), thickness, cv::LINE_8); 
00314 
00315         curRadians+=radianStep;
00316     }
00317     
00318 }
