00376 void Visualizer::drawSpectrogram(){
00377 
00378     const double FREQ_MIN = (double)cfg.iavconf.minFrequency; 
00379     const double FREQ_MAX = (double)cfg.iavconf.maxFrequency;
00380     int SR = cfg.audconf.sampleRate.load();
00381     int W = cfg.dispconf.dispResW.load();
00382     int H = cfg.dispconf.dispResH.load();
00383     int numAudioSamples = spectrogram.get_numAudioSamples();
00384 
00385     float min_magnitude,max_magnitude;
00386     spectrogram.readBatch(specMagnitude,min_magnitude,max_magnitude);
00387 
00388     for (size_t i = 0; i < specMagnitude.size() ; ++i) {
00389 
00390         float magnitude = specMagnitude[i];
00391         
00392         // Minmax magnitude's normalization  
00393         int normalized_magnitude = static_cast<int>( 
00394                                         std::min( 
00395                                             (float)((magnitude - min_magnitude) / (max_magnitude - min_magnitude) * static_cast<float>(H) * 0.5),
00396                                             static_cast<float>(H)/2.f
00397                                         )
00398                                     );
00399 
00400         // Calculate the frequency interval for each bin
00401         double freq_bin = (SR / 2.0) * (static_cast<double>(i) / static_cast<double>(numAudioSamples / 2.0)); // Frequency of the current bin
00402 
00403         // filtering the frequencies outside the scope of the iav application
00404         if (freq_bin >= FREQ_MIN && freq_bin <= FREQ_MAX) {
00405             
00406             int x = static_cast<int>((freq_bin - FREQ_MIN) / (FREQ_MAX - FREQ_MIN) * W);
00407             
00408             // Create a the drawing line
00409             int line_length = normalized_magnitude;
00410             int top = H / 2 - line_length;
00411             int bottom = H / 2 + line_length;
00412         
00413             for (int y = top; y <= bottom; ++y) {
00414                 // Ensure not out-of-bounds 
00415                 if (y >= 0 && y < H && x >= 0 && x < W) {
00416                     visualFrame.at<cv::Vec3b>(y,x)[0] = 255 - B;
00417                     visualFrame.at<cv::Vec3b>(y,x)[1] = 255 - G;
00418                     visualFrame.at<cv::Vec3b>(y,x)[2] = 255 - R;
00419 
00420                 }
00421             }
00422 
00423         }
00424     }
00425     
00426 }
